        var mins = 0;
        
        let time = '';


        setInterval( function TimerInterval() {
            secs++;
            
            if(secs > 60) {
                secs = 0;
                mins++;
            }

            time = `${mins}:${secs}`;

            document.getElementById('time').innerHTML = `Partie en cours: ${time}`;
        }, 1000);

        setTimeout(async () => {
            await clearInterval()
