import './counter.css';

const GameCounter = () => {
    Timer();
    function Timer() {
        var secs = 0;
        var mins = 0;
        
        let time = '';

        setInterval(async () => {
            secs++;
            console.log(secs)
            
            if(secs > 60) {
                secs = 0;
                mins++;
            }

            time = `${mins}:${secs}`;

            document.getElementsByClassName('timer').innerHTML = `Temps: ${time}`;
        }, 1000);
    }

    return(
    <div className='bottom'>
        <a className='timer'></a>
    </div>
    );
}

export default GameCounter;
