import './counter.css';

const GameCounter = () => {
    Timer();
    function Timer() {
        var secs = 0;
        var mins = 0;
        var id = setInterval(function() { 
            secs++;
            if(secs > 60) {
                secs = 0;
                mins++;
            }
            document.getElementsByClassName("timer").innerHTML = ++secs;
        }, 1000);
    }

    return(
    <div className='bottom'>
        <a className='timer'>Temps: 00:00</a>
    </div>
    );
}

export default GameCounter;
