import './counter.css';

const GameCounter = () => {
    function Timer() {
        var secs = 0;
        var id = setInterval(function(){ 
            secs++; console.log(secs);
            if(secs> 5){
                clearInterval(id);
                alert('Total Time: ' + secs + ' seconds');
           }
        }, 1000);
    }

    <div className='timer'>
        <a className='time'>0</a>
    </div>
}

export default GameCounter;
